var pedro = new Howl({src: ['assets/audio/pedro.wav'], loop: true});
var pedro_isPlaying = false;

$('#logo').click(function() {
    if(pedro_isPlaying) {
        pedro.stop();
        $('#logo').removeClass('rotate');
    } else {
        pedro.play();
        setTimeout(function() {
            $('#logo').addClass('rotate');
        }, 1000)
    }
});