
var socialmedia = [
    {
        prefix:'https://x.com/Official',
        suffix:'',
        icon:'twitter',
        position: 'suffix',
        color:'#1DA1F2',
        link: 'https://x.com/OfficialChroma9'
    },


]
$("#name-region").hover(function() {
    var s = socialmedia[Math.floor(Math.random() * socialmedia.length)];
    $('#name-prefix').text(s.prefix);
    $('#name-suffix').text(s.suffix);
    $('#name-link').attr('href', s.link);
}, function() {
    $('#name-prefix').text('');
    $('#name-suffix').text('');
    $('#name-link').attr('href', '/');
});